import React, { useState } from "react";


const App = () => {

const [name,setname] = useState();
const [fullname ,setfullname] = useState();

const onsubmit = (event) =>{
    event.preventDefault();
    setfullname(name)
}

const clicked = (event) => { 
setname(event.target.value)
}

return(
    <>
    <div className="container">
    <h1 className="head">Hello {name}</h1>
        <form onSubmit={onsubmit}>
            <input type="text" value={name} placeholder="Enter Your Firstname" className="fname" onChange={clicked}></input>
            {/* <input type="text" value={name} placeholder="Enter Your Lastname" className="lname"></input> */}
        </form>
            <button className="btn" type="submit"> Submit Now</button>
    </div>
    </>
)
}


export default App;
