import React, { useState } from "react";


const App = () => {

//Here two usestate is called first one is to set the input data means the recent data which is being input by the user now and the 2nd one is to update the data means a list will be available there inside the items variable and setIte0ms is used to set the value of the new list by adding the new item which is recently added by the user. 


const [inputlist,setInputList] = useState();
const [items,setItems] = useState([]);




const eventchanged = (event) => {
    setInputList(event.target.value);
}



// Here 
const clicked = () =>{
    setItems((olditems)=>{
        return [...olditems,inputlist]
    })
    setInputList("");
}



return(
    <>
        <div className="main_div_2">
            <h1 className="head">TODO LIST</h1>
            <br/>
            <input type="text" placeholder="Add Work" value={inputlist} onChange={eventchanged}/>
            <button className="btn" onClick={clicked}>+</button>



            <ul>
            {items.map((itemsVal)=>{
                return <li>{itemsVal}</li>
            })}
            </ul>
   
   
        </div>
    </>
)

}


export default App;
