import React from "react";
import Netflix from "./Netflix";
import Hotstar from "./Hotstar";
import Amazon from "./Amazon";

const choice ="netflix";
// const choice = "hotstar";
// const choice = "amazon"; 

function Choice (){
if(choice === "netflix"){
    return (
        <>
        <Netflix/>
        </>
    );
}
else {
    return (
    <>
    <Hotstar/>
    </>
    )
}
}

function Choice2(){
    if(choice === "amazon"){
        return (
            <>
            <Amazon/>
            </>
        )
    }
}


export default Choice;
