import React, { useState } from "react";
import Button from '@material-ui/core/Button';
import List2 from "./ListRender";

const App = () =>{
    const [input,setInput] = useState();
    const [worklist,setWorklist] = useState([]);


    const eventChanged = (event) =>{
        setInput(event.target.value)
    }

    const clicked = () =>{
        setWorklist((olditems)=>{
            return [...olditems,input]
        })
        setInput("");
    }

    
    const deleteItems = (id) =>{

      setWorklist((olditems)=>{
        return olditems.filter((arrayelement,index)=>{
            return index !==id;
        })
    })
    }
    

    return (
        <>
        <div className="container">
            <h1 className="head">TODO LIST</h1>

            <input  placeholder="Enter Work" type="text" onChange={eventChanged} value={input}  />
            
            <span className="btn_div">
            <Button style={{color:"red",fontWeight:"bolder",border:"1px solid red",fontSize:"15px"}} onClick={clicked}>ADD</Button>
            </span>

            <ul>
            {worklist.map((itemsVal,index)=>{
                return <List2 key={index}  id={index} onSelect={deleteItems} text={itemsVal} />;
            })}
            </ul>
        </div>
        </>
    )
}

export default App;
