import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';


const Form = (props) =>{

    const [inputNote,setInputNote]=useState(
       { title:"",
        content:"",}
    );


    const [expand,setExpand] =  useState(false);


    const EventOccured1 = (event) =>{
        const {name,value} = event.target;

        setInputNote((prevdata) =>{
            return {
                ...prevdata,
                [name]:value,
            }
        })
    }





        const clicked = ()=> {
            props.Passnote(inputNote);
            setInputNote({
                title:"",
                content:"",
            });
        }
     
        
    return (
        <>
        <div className="form_box">
            <form className="form">
              {expand?
                <input type="text" placeholder="Title"  name="title" value={inputNote.title} onChange={EventOccured1}/>:null}
                <br/>
                <textarea type="text" cols="" placeholder="Enter Your Content" name="content" value={inputNote.content} onChange={EventOccured1} onClick={Expandit}></textarea>
            </form>
            <button className="add_btn" onClick={clicked}><AddIcon/></button>
        </div>
        </>
    )
}

export default Form;
