import React, { useState } from "react";

const App = () =>{

    const [value,setValue] = useState(0);

    const change_event = (event) =>{

    }
    
    const clicked1 = () =>{
        setValue(value+1);
    }
    
    const clicked2 = () =>{
        if(value!==0)
        setValue(value-1); 
    }

    const clicked3 = () =>{
        setValue(value/2); 
    }

    const clicked4 = () =>{
        setValue(value*2); 
    }


    return (
        <>
        <div className="container">
            <h1 className="head" onChange={change_event}>{value}</h1>
            <button className="btn" onClick={clicked1}>Increase</button>
            <button className="btn" onClick={clicked2}>Decrease</button>
            <button className="btn" onClick={clicked3}>Half</button>
            <button className="btn" onClick={clicked4}>Twice</button>
        </div>
        </>
    )
}

export default App;
