import React from "react";
import ReactDom from "react-dom";
import "./index.css"

var Greeting ="";
var content = {}
var date = new Date();
var hour = date.getHours();
if (hour >= 1 && hour < 12){
    Greeting = "Good Morning ";
    content.color = "blue"
}
else if (hour >= 12 && hour < 18){
    Greeting = "Good Afternoon";
    content.color = "green"
}
else {
    Greeting = "Good Night";
    content.color = "red"
}


ReactDom.render(
    <div className="container">
    <h1>Hello Sir ,<span style={content}>{Greeting}</span></h1>
    </div>,document.getElementById("root")
);
