import React, { useState } from "react";
import "./index.css";




const Clock = () =>{
    let time = new Date().toLocaleTimeString();
    const [currenttime,settime] = useState(time);

const update = () =>{
   let time =new Date().toLocaleTimeString(); 
    settime(time);
}

setInterval(update,1000)

return(
    <>
    <div className="container3">
        <h1>{currenttime}</h1>
    </div>
    </>
)
}



export default Clock;
