import React, { useState } from "react";



const App = () =>{

const [name,setName] = useState();
const [fullname,setFullname] = useState();


const change1 = (event) => {
setName(event.target.value);
    }


const change2 = (event) => {
setFullname(event.target.value);   
}

const submited = (event) => {
   event.preventDefault();
    }


return(
    <>

    <div className="container">
    <h1 className='head'>Hello {name} {fullname}</h1>
    <form  onSubmit={submited}>
        <input type="text" placeholder="Enter Your First Name" onChange={change1} ></input>
        <input type="text" placeholder="Enter Your Last Name" onChange={change2} ></input>
        </form>
        <button className="btn" type="submit">Submit Now</button>
    </div>
    </>
)

}



export default App;
