        })

    }

    const submited = (event) =>{
        event.preventDefault();
        alert("Form Submited Sucessfully!")
    }

    return(
        <>
        <div className="container">
            <h1 className="head">Hello {fullname.fname} {fullname.lname}</h1>
            <p>{fullname.email}</p>
            <form >
                <input type="text" placeholder="Enter Your First Name" onChange={event_on_change} value={fullname.fname} name="fname"></input>

                <input type="text" placeholder="Enter Your Last Name" onChange={event_on_change} value={fullname.lname} name="lname"></input>

                <input type="email" placeholder="Enter Your Email" onChange={event_on_change} value={fullname.email} name="email" autoComplete="off"></input>

                <input type="number" placeholder="Enter Your Phone Number" onChange={event_on_change} value={fullname.phone} name="phone" autoComplete="off"></input>

                <textarea type="textarea" placeholder="Enter Your Querry" onChange={event_on_change} value={fullname.textarea} name="textarea"/>

            </form>
            <button className="btn" type="submit" onClick={submited}>Submit Now</button>
        </div>
    </>
    )
}



export default Practice;
