import React, { useState } from "react";

const App = () =>{

    const [value,setValue] = useState(0);

    
    
    const clicked1 = () =>{
        setValue(value+1);
    }
    
    const clicked2 = () =>{
        if(value>0){
            setValue(value-1); 
        }
        else {
            alert("Limit Zero Has Reached!!")
            setValue(0);
        }
    }

    const clicked3 = () =>{
        setValue(value/2); 
    }

    const clicked4 = () =>{
        setValue(value*2); 
    }

    const clear = ()=>{
        setValue(0);
    }

    return (
        <>
        <div className="container">
            <h1 className="head">{value}</h1>
            <button className="btn" onClick={clicked1}>Increase</button>
            <button className="btn" onClick={clicked2}>Decrease</button>
            <button className="btn" onClick={clicked3}>Half</button>
            <button className="btn" onClick={clicked4}>Twice</button>
            <button className="btn" onClick={clear}>Clear</button>
        </div>
        </>
    )
}

export default App;
