import React, { useState } from "react";

const App2 = () =>{
    const [num1,setNum1] = useState();
    const [num2,setNum2] = useState();
    const [sum,setSum] = useState();


    const event_add_1 = (event) =>{
        setNum1(event.target.value);
    }

    const event_add_2 = (event) =>{
        setNum2(event.target.value);
    }

    const event_sum = () =>{
        setSum(num1+num2)
    }

return(
    <>
    <div className="container2">
        <h1 className="head2">CACULATOR</h1>
        <input type="number"  className="inputed_data" onChange={event_add_1}>{num1}</input>
        <input type="number" className="inputed_data" onChange={event_add_2}>{num2}</input>
        <input type="number" className="inputed_data" onChange={event_sum}>{sum}</input>
    </div>
    </>
)
}

export default App2;
