import React, { useState } from "react";


const App2 = () => {

    const [fname,setFname] = useState();
    const [firstname,setFirstname] = useState();

    const [lname,setLname] = useState();
    const [lastname,setLastname] = useState();



const change_event_1 = (event) =>{
setFname(event.target.value);
}

const change_event_2 = (event) =>{
setLname(event.target.value);
}

const submited = (event) =>{
setFirstname(fname);
setLastname(lname)
}

    return(
    <>
        <div className="container">
        <h1 className="head">Hello {firstname} {lastname}</h1>
        <form >
            <input  onChange={change_event_1} type="text" value={fname}  placeholder="Enter Your First Name"></input>
            <input  onChange={change_event_2} type="text" value={lname}  placeholder="Enter Your Last Name"></input>
        </form>
        <button onClick={submited} className="btn">Submit Now</button>
        </div>
    </>
);


}


export default App2;
