import React, { useState } from "react";
import List2 from "./List";


const App = () => {

//Here two usestate is called first one is to set the input data means the recent data which is being input by the user now and the 2nd one is to update the data means a list will be available there inside the items variable and setIte0ms is used to set the value of the new list by adding the new item which is recently added by the user. 

const [inputlist,setInputList] = useState();
const [items,setItems] = useState([]);



// Its a onchange function means when the user will type smething inside the list then  it will perform certain function so that current value is displayed on the screen.

const eventchanged = (event) => {
    setInputList(event.target.value);
}



// It is the click function when the button is clicked it start to work and in this function setItems is a hook for updating the list so olditem is a variable and it returns the ...olditems (spread operators) along with the recent data which is being added by the user now.

// In the last step setInputList is  called this is because to clear the recent data which is shown now in the input line. 

const clicked = () =>{
    setItems((olditems)=>{
        return [...olditems,inputlist]
    })
    setInputList("");
}



return(
    <>
        <div className="main_div_2">
            <h1 className="head">TODO LIST</h1>
            <br/>
            <input type="text" placeholder="Add Work" value={inputlist} onChange={eventchanged}/>
            <button className="btn" onClick={clicked}>+</button>



            <ul>
            {items.map((itemsVal,index)=>{
                return <List2 key={index}  id={index} onSelect={deleteItem} text={itemsVal} />;
            })}
            </ul>
   
   
        </div>
    </>
)

}


export default App;
