    greeting="Good Morning"
        }

else if(hour>=12 && hour<=18){
    greeting="Good Afternoon"
}
else{
    greeting="Good Night";
}

var container = {
    // textAlign:"center"
    display:"flex"
}
var content = {}

ReactDom.render(
    <>
    <div style={container}>
    <h1 >Hello sir, <span style={content}>{greeting}</span> </h1>
    </div>
    </>,document.getElementById("root")
