import React, { useState } from "react";
import "./index.css"



const App = () => {
    time = new Date().toLocaleDateString();
    const [time , settime] = useState(time);

    const UpdateTime = () =>{
       let  newtime = new Date().toLocaleDateString();
        settime(newtime)
    }

    return(
        <>
        <div className="container">
            <h1>{time}</h1>
            <button onClick={UpdateTime}>Get Time</button>
        </div>
        </>
    )
}






export default App;
