import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import { DialogTitle } from "@material-ui/core";

const Form = () =>{

    const [inputNote,setInputNote]=useState(
       { title:"",
        content:"",}
    );

    const EventOccured1 = (event) =>{
        setInputNote(event.target.value)
    }

    const EventOccured2 = (event) =>{
        setInputContent(event.target.value)
    }

        const clicked = ()=> {
            setInputNote("")
        }
        
    return (
        <>
        <div className="form_box">
            <form className="form">
                <input type="text" placeholder="Title" value={inputNote.title} onChange={EventOccured1}/>
                <br/>
                <textarea type="text" cols="" placeholder="Enter Your Content" value={inputNote.content} onChange={EventOccured1}></textarea>
            </form>
            <button className="add_btn" onClick={clicked}><AddIcon/></button>
        </div>
        </>
    )
}

export default Form;
