import React from "react";
import ReactDom from "react-dom";



var container = {
    textAlign:"center",
    marginTop:"20%"
}
var content = {}
var greeting=""
var date = new Date();
var hour = date.getHours();
if(hour>=1 && hour<=12){
    greeting="Good Morning";
    content.color = "green"

}else if(hour>=12 && hour<=18){
    greeting="Good Afternoon";
    content.color ="yellow"
}else{
    greeting="Good Night";
    content.color = "red"
}


ReactDom.render(
    <>
    <div style={container}>
    <h1 >Hello sir, <span style={content}>{greeting}</span> </h1>
    </div>
    </>,document.getElementById("root")
);
