import React, { useState } from "react";


const ComplexForm = () => {

    const [fullname, setFullname] = useState({
        fname: "",
        lname: "",
        email: "",
        phone: "",
    });


    const event_on_change = (event) => {
        const { name, value } = event.target;

        setFullname( (prevalue) =>{

            if(name === "fname"){
                return{
                    fname:value,
                    lname:prevalue.lname,
                    email:prevalue.email,
                    phone:prevalue.phone,
                }
            }
        })
    }



    const submited = (event) => {
        event.preventDefault();
        alert("Form submitted sucessfully")
    }


    return (
        <>
            <div className="container">
                <h1 className="head">Hello</h1>
                <form onSubmit={submited}>
                    <input type="text" placeholder="Enter Your First Name" onChange={event_on_change} value={fullname.fname}></input>
                    <input type="text" placeholder="Enter Your Last Name" onChange={event_on_change} value={fullname.lname}></input>
                    <input type="email" placeholder="Enter Your Email" onChange={event_on_change} value={fullname.email}></input>
                    <input type="number" placeholder="Enter Your Phone Number" onChange={event_on_change} value={fullname.phone}></input>
                </form>
                <button className="btn" type="submit">Submit Now</button>
            </div>
        </>
    )

}

export default ComplexForm;
