import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';

const Form = () =>{

    const [inputNote,setInputNote]=useState();
    const [inputContent,setInputContent] = useState();

    const EventOccured1 = (event) =>{
        setInputNote(event.target.value)
    }

    const EventOccured2 = (event) =>{
        setInputContent(event.target.value)
    }

        const clicked = ()=> {
           alert("Note Added!!")
            setInputNote("")
            setInputContent("")
        }
    return (
        <>
        <div className="form_box">
            <form className="form">
                <input type="text" placeholder="Title" value={inputNote} onChange={EventOccured1}/>
                <br/>
                <textarea type="text" cols="" placeholder="Enter Your Content" value={inputContent} onChange={EventOccured2}></textarea>
            </form>
            <button className="add_btn" onClick={clicked}><AddIcon/></button>
        </div>
        </>
    )
}

export default Form;
