import React, { useState } from "react";



const App = () => {

    // const state = useState;

    const [count, setCount] = useState(0);
    
    const Incnum = () => {
        setCount(count+1);
    } 


    return(
        <>
            <div className="container">
                <h1>{count}</h1>
                <button onClick={Incnum}>CLICK ME</button>
            </div>
        </>
    )
}



export default App;
