import React, { useState } from "react";


const App3 = () => {
    const [name , setName] = useState();
    const [fullname ,setFullname] = useState();
    
    const change = (event) => {
        setName(event.target.value)
    }

    const click = () => {
        setFullname(name)
    }

    return (
        <>
        <div className="container">
        <h1 className="head">Hello {fullname}</h1>
        <input type="text" placeholder="Enter Your First Name" value={name} onChange={change}></input>
        <input type="password" placeholder="Enter Your Last Name" ></input>
        <button onClick={click}>Submit Now</button>
        </div>
        </>
    )
}



export default App3;
