import React, { useState } from "react";
import Button from '@material-ui/core/Button';


const App = () =>{
    const [input,setInput] = useState();
    const [worklist,setWorklist] = useState([]);


    const eventChanged = (event) =>{
        setInput(event.target.value)
    }

    const clicked = () =>{
        setWorklist((olditems)=>{
            return [...olditems,input]
        })
    }

    return (
        <>
        <div className="container">
            <h1 className="head">TODO LIST</h1>

            <input  placeholder="Enter Work" type="text" onChange={eventChanged} value={input}  />
            
            <span className="btn_div">
            <Button style={{color:"red",fontWeight:"bolder",border:"1px solid red",fontSize:"15px"}} onClick={clicked}>ADD</Button>
            </span>

            <ul>
                <li>{worklist}</li>
            </ul>
        </div>
        </>
    )
}

export default App;
