import React, { useState } from "react";

const App2 = () =>{
    const [num1,setNum1] = useState();
    const [num2,setNum2] = useState();
    const [sum,setSum] = useState();
  
    const [num3,setNum3] = useState();
    const [num4,setNum4] = useState();
    const [sub,setSub] = useState();

    const [num5,setNum5] = useState();
    const [num6,setNum6] = useState();
    const [mul,setMul] =useState();

    const [num7,setNum7] = useState();
    const [num8,setNum8] = useState();
    const [div,setDiv] =useState();

    const event_add_1 = (event) =>{
        setNum1(event.target.value);
    }
    
    const event_add_2 = (event) =>{
        setNum2(event.target.value);
    }
    const event_clicked = () =>{
        setSum( Number(num1)+Number(num2));
    }
     

    const event_sub_3 = (event) =>{
        setNum3(event.target.value);
    }
    const event_sub_4 = (event) =>{
        setNum4(event.target.value);
    }
    
   const event_clicked2 = () =>{
       setSub( Number(num3)-Number(num4));
   }


   
   const event_mul_5 = (event) =>{
    setNum5(event.target.value);
}
const event_mul_6 = (event) =>{
    setNum6(event.target.value);
}

const event_clicked3 = () =>{
   setSub( Number(num5)*Number(num6));
}



const event_div_7 = (event) =>{
    setNum7(event.target.value);
}
const event_div_8 = (event) =>{
    setNum8(event.target.value);
}

const event_clicked4 = () =>{
   setDiv( Number(num7)/Number(num8));
}


   const clear = () =>{
       setNum1("");
       setNum2("");
       setNum3("");
       setNum4("");
       setNum5("");
       setNum6("");
       setSum("");
       setSub("");
       setMul("");
       setDiv("");

   }

return(
    <>
    <div className="container2">
        <h1 className="head2">CACULATOR</h1>

        <div className="add" id="operation">
        <input type="text"  className="inputed_data" onChange={event_add_1} value={num1}></input>
        <span>+</span>
        <input type="text" className="inputed_data" onChange={event_add_2} value={num2}></input>
        <button className="btn2" onClick={event_clicked}>ADD</button>
        <h1>{sum}</h1>
        <button className="btn65" onClick={clear}>Clear</button>
        </div>
        

        <div className="sub"  id="operation">
        <input type="text"  className="inputed_data" onChange={event_sub_3} value={num3}></input>
        <span className="sub_style">_</span>
        <input type="text" className="inputed_data" onChange={event_sub_4} value={num4}></input>
        <button className="btn2" onClick={event_clicked2}>SUB</button>
        <h1>{sub}</h1>
        </div>


        
        <div className="mul"  id="operation">
        <input type="text"  className="inputed_data" onChange={event_mul_5} value={num5}></input>
        <span className="sub_style2">X</span>
        <input type="text" className="inputed_data" onChange={event_mul_6} value={num6}></input>
        <button className="btn2" onClick={event_clicked3}>Multiply</button>
        <h1>{mul}</h1>
        </div>

        
        
        <div className="div"  id="operation">
        <input type="text"  className="inputed_data" onChange={event_div_7} value={num7}></input>
        <span className="sub_style2">/</span>
        <input type="text" className="inputed_data" onChange={event_div_8} value={num8}></input>
        <button className="btn2" onClick={event_clicked4}>Multiply</button>
        <h1>{div}</h1>
        </div>



    </div>
    </>
)
}

export default App2;
