import React, { useState } from "react";
import "./index.css"




const state = useState();

const [count , setCount] = useState(0);


const DECREMENT = () => {
    setCount(count + 100)
}


const Hooks1 = () => {
    return (
        <>
        <div className="container">
            <h1>{count}</h1>
            <button onClick={DECREMENT}></button>
        </div>
        </>
    );
}





export default  Hooks1;
