import React ,{useState} from "react";


const ComplexApp = () =>{

const [fullname,setfullname] = useState({
    fname:"",
    lname:"",
});


const change_event = (event) =>{
    const value = event.target.value;
    const name = event.target.name;

    setfullname( (prevalue) => {

        if (name === "fname"){
           return{
            fname : value,
            lname : prevalue.lname,
           };
        }

        else  if (name === "lname"){
            return{
             fname : prevalue.fname,
             lname : value,
            };
         }


    })
}

const submited = (event) =>{
    event.preventDefault();
    alert("Form Submitted Sucessfully")
}
    return (
        <>
         <div className="container">
        <h1 className="head">Hello {fullname.fname} {fullname.lname} </h1>
        <form  onSubmit={submited} >
            <input  onChange={change_event} name="fname" type="text" value={fullname.fname}  placeholder="Enter Your First Name"></input>
            <input  onChange={change_event} name="lname" type="text" value={fullname.lname}  placeholder="Enter Your Last Name"></input>
        </form>
        <button  className="btn">Submit Now</button>
        </div>
        </>
    )
}

export default ComplexApp;
